import { Component, Input } from '@angular/core';
import { Character } from '../../interface/character.interface';

@Component({
  selector: 'app-dbz-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {

  @Input()
  public characterList: Character[] = [];
}
<ul class="list-group ">
  <li *ngFor="
    let character of characterList;
    let i = index;
    let isFirt = first;
    let isLast = last;
    let isEven = even;
    let isOdd = odd;"
    class="list-group-item d-flex"
    [ngClass]="{
      'list-group-item-dark':isLast,
      'list-group-item-primary':isEven,
    }">
    <!--
      class="list-group-item list-group-item-success">
      class="list-group-item {{ i === 0 ? 'list-group-item-success' : ''}}">
     -->
    <span class="text-primary"> Posición: {{i + 1}}. </span>
    <span>{{character.name}}</span>
      -
    <strong>Power: </strong> <span>{{character.power}}</span>
<button class="btn btn-danger">X</button>
    <!-- <span>. Es el primero: {{ isFirt }} . </span>
    <span>Es el ultimo: {{ isLast }}</span> -->
  </li>
</ul>
