import { Component, EventEmitter, Output } from '@angular/core';
import { Character } from '../../interface/character.interface';

@Component({
  selector: 'dbz-add-character',
  templateUrl: './add-character.component.html',
  styleUrls: ['./add-character.component.css']
})
export class AddCharacterComponent {

  @Output()
  public onNewCharacter: EventEmitter<Character> = new EventEmitter();

  public character: Character = {
    name: '',
    power: 0
  };

public emitCharacter():void {
    console.log(this.character);
    if(this.character.name.length === 0) {
      console.log('Invalid name');
    }
    onNewCharacter
    this.character.name = '';
    this.character.power = 0;
  }
}
<h4>Agregar personaje</h4>
<form class="row"
(ngSubmit)="emitCharacter()">
  <!-- <input type="text" [value]="character.name" class="form-control mb-2" placeholder="Nombre"> -->
  <!-- Para que funcione el twoway-databinding (caja de bananas porque asocia un
    evento () y un valor []) hay que importar la librería FormsModule
  Como los modelos están dentro de un formulario (tag <form>)
    se debe establecer el nombre del campo del que tomará el valor
  -->
  <input
    type="text"
    [(ngModel)]="character.name"
    name="name"
    class="form-control mb-2"
    placeholder="Nombre">
  <input
    type="number"
    [(ngModel)]="character.power"
    name="power"
    class="form-control mb-2" placeholder="Poder">
  <!-- al ponerlo de tipo submit, aunque no hga nada se
  llama al servidor -->
  <button type="submit" class="btn btn-primary">Agregar</button>
  <!-- <button type="submit" (click)="emitCharacter()" class="btn btn-primary">Agregar</button> -->

  <pre>{{character | json}}</pre>
</form>
