import { Component, OnInit } from '@angular/core';
import { Character } from '../interface/character.interface';

@Component({
  selector: 'app-dbz-main-page',
  templateUrl: './main-page.component.html',
})

export class MainPageComponent {

  public characters : Character[] = [
    {
      name: 'Kriling',
      power: 500,
    }, {
      name: 'Goku',
      power: 9500,
    }, {
      name: 'Vegeta',
      power: 7500,
    }
  ];

  onNewCharacter(character: Character):void {
    console.log("Main page " + character);

  }
}
<hr>

<div class="row">
  <div class="col">
    <!-- al pasarle la lista de characteres como @Input a la lista que
      renderiza esa lista, que esté representado
      por el selector <app-dbz-list> entonces es accesible a ese componente
    -->
    <app-dbz-list [characterList]="characters"></app-dbz-list>

  </div>
  <div class="col">
    <dbz-add-character (onNewCharacter)="onNewCharacter"></dbz-add-character>
  </div>
</div>
