import { Component, OnInit } from '@angular/core';
import { Character } from '../interface/character.interface';
import { DbzService } from '../services/dbz.service';

@Component({
  selector: 'app-dbz-main-page',
  templateUrl: './main-page.component.html',
})

export class MainPageComponent {

constructor(private dbzService: DbzService) {}

get characters(): Character[] {
  return [ ...this.dbzService.characters];
}



}
<hr>

<div class="row">
  <div class="col">
    <!-- al pasarle la lista de characteres como @Input a la lista que
      renderiza esa lista, que esté representado
      por el selector <app-dbz-list> entonces es accesible a ese componente
    -->
    <app-dbz-list [characterList]="dbzService.characters"
    (onDeleteId)="dbzService.deleteCharacterById($event)"></app-dbz-list>

  </div>
  <div class="col">
    <dbz-add-character
      (onNewCharacter)="dbzService.onNewCharacter($event)"
     ></dbz-add-character>
  </div>
</div>
