import { Component } from '@angular/core';

@Component({
  selector: 'app-heroes-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {

  public heroNames : string[] = [  'Spiderman', 'Thor','Hulk','Ironman'];

  public deletedHero?: string;

  removeLastHero() : void {
    this.deletedHero = this.heroNames.pop();
    console.log(this.deletedHero);
  }
}
<h3>Listado de Heroes</h3>
<hr>

<div *ngIf="deletedHero">
  <h3> Héroe borrado fue <small class="text-danger">{{deletedHero}}</small></h3>
</div>
<ng-template>
  <h3> No ha borrado nada.</h3>
</ng-template>

<button
  (click)="removeLastHero()"
  class="btn btn-outline-danger">
  Borrar ultimo heroe
</button>


<ul class="mt-2 list-group">
  <li *ngFor="let name of heroNames"class="list-group-item">{{ name }}</li>
</ul>
